import React from 'react';
import { 
  Box, 
  Paper, 
  Typography, 
  List, 
  ListItem, 
  ListItemText, 
  IconButton,
  Divider
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';

const Cart = ({ items, onRemoveItem }) => {
  return (
    <Paper 
      elevation={3} 
      sx={{ 
        width: '300px',
        height: '100%',
        p: 2,
        backgroundColor: 'white',
        borderRadius: '12px'
      }}
    >
      <Typography variant="h6" sx={{ mb: 2 }}>
        Shopping Cart ({items.length})
      </Typography>

      {items.length === 0 ? (
        <Typography variant="body2" color="text.secondary">
          Your cart is empty
        </Typography>
      ) : (
        <List>
          {items.map((item, index) => (
            <React.Fragment key={index}>
              <ListItem
                secondaryAction={
                  <IconButton 
                    edge="end" 
                    aria-label="delete"
                    onClick={() => onRemoveItem(index)}
                  >
                    <DeleteIcon />
                  </IconButton>
                }
              >
                <ListItemText
                  primary={item.part_name}
                  secondary={
                    <React.Fragment>
                      <Typography
                        component="span"
                        variant="body2"
                        color="text.primary"
                      >
                        Part #: {item.part_id}
                      </Typography>
                      <br />
                      <Typography
                        component="span"
                        variant="body2"
                        color="text.secondary"
                      >
                        {item.appliance_type}
                      </Typography>
                    </React.Fragment>
                  }
                />
              </ListItem>
              {index < items.length - 1 && <Divider />}
            </React.Fragment>
          ))}
        </List>
      )}
    </Paper>
  );
};

export default Cart;
